//replaces any characters in the query string that are not a space or a-z, A-Z, 0-9 with an underscore
function getGoogleAdTargetingUrl() {
    var path = location.pathname + location.search;
    var targetingUrl = path;

    //if there is a query string
    var index = path.indexOf('?');
    if (index != -1) {
        var prefix_path = path.substring(0, index);
        var suffix_path = path.substring(index, path.length).replace(/[^\w\s]/gi, '_');
        targetingUrl = prefix_path + suffix_path;
    }

    // remove .aspx from the url for targeting
    targetingUrl = targetingUrl.replace('.aspx', '');

    //return targeting url
    return targetingUrl;
}

//get the ad targeting url
var targetingUrl = getGoogleAdTargetingUrl();

//a collection of google ad slots
var googleAdSlots = new Array();

/*
    Modified version of define slot which replaces {0} with language code
    and adds slot to googleAdSlots collection.
    Also calls .addService(googletag.pubads()) and sets page level targeting.
*/
googletag.cmd.push(function () {
    googletag.defineSlotRef = googletag.defineSlot;
    googletag.defineSlot = function (unitName, size, divId) {
        unitName = unitName.replace("{0}", "eng");
        var slot = googletag.defineSlotRef(unitName, size, divId).addService(googletag.pubads()).setTargeting("Page", targetingUrl);
        googleAdSlots.push(slot);
        return slot;
    };
});

googletag.cmd.push(function () {
    googletag.defineOutOfPageSlotRef = googletag.defineOutOfPageSlot;
    googletag.defineOutOfPageSlot = function (unitName, divId) {
        unitName = unitName.replace("{0}", "eng");
        var slot = googletag.defineOutOfPageSlotRef(unitName, divId).addService(googletag.pubads()).setTargeting("Page", targetingUrl);
        googleAdSlots.push(slot);
        return slot;
    };
});

